import { Component } from "react";
import React, { useState, useEffect } from "react";
import api from "../api";
import { Link } from "react-router-dom";

function Show() {
  const [contatos, setContato] = useState();
  const [search, setSearch] = useState();
  const [id, setId] = useState(0);

  const pesquisar = {
    id: id,
  };
  
  state = {
    contatos: [],
  };

  useEffect(() => {
    handleContato();
  }, []);

  async function handleContato() {
    const response = await api.get("/contatos/find/" + id);

    if (response.status !== 204) {
      setContato(response.data);
    }
  }

  async function handleSubmit(e) {
    e.preventDefault();
    await pesquisa(pesquisar);
  }
  async function pesquisa(idPesquisa) {
    console.log(idPesquisa);
    const response = await api.get("/contatos/" + id);
    if (response.status == 200) {
      this.setState({ contatos: responseContatos.data });
    }
  }
  return (
    <>
      <Link to="/">
        <button type="button" className="botao-voltar">
          Voltar
        </button>
      </Link>
      <form onSubmit={(e) => handleSubmit(e)}>
        Informe o ID do contato que deseja mostrar:
        <input
          type="number"
          name="id"
          onChange={(value) => setId(value.target.value)}
          value={id}
        />
        <button type="submit">Submit</button>
      </form>
      <p>
        {console.log(contatos)}
        {contatos?.map((contato) => (
          <span key={contato.id}>
            <p>
              <span>ID: </span>
              {contato.id}
            </p>
            <p>
              <span>Name: </span>
              {contato.name}
            </p>
            <p>
              <span>Email: </span>
              {contato.email}
            </p>
            <p>
              <span>Telefone: </span>
              {contato.telefone}
            </p>
          </span>
        ))}
      </p>
    </>
  );
}
export default Show;
